void assertNoPendingJniExceptionIf(JNIEnv* env, bool condition) {
  if (!condition) {
    return;
  }

  if (env->ExceptionCheck() == JNI_TRUE) {
    assertNoPendingJniException(env);
    return;
  }

  throw YogaJniException();
}

